def.body:
if str.is_a?(Number)
  return Subnet::IPv4.new(ntoa(str))
end
Subnet::IPv4.new(str)

@allocator = @allocator + (1 + skip)
next_ip = network_u32 + @allocator
if next_ip > (broadcast_u32 + 1)
  return Iterator::Stop::INSTANCE
end
IPv4.parse_u32(network_u32 + @allocator, @prefix)

if other.is_a?(Number)
  @prefix + other
else
  @prefix + other.prefix
end

if other.is_a?(Number)
  @prefix - other
else
  (@prefix - other.prefix).abs
end
